package array;

public class Pppp {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		int []array= {5,5,5,5,5};
		
		int sum=0;
		
		for(int i=0; i<array.length; i++)
		{
			sum=sum+array[i];
			
		}
		
		System.out.println("sum="+sum);
		
		System.out.println("average="+(sum/array.length));
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
	}

}
